  @override
  void initState() {
    super.initState();
    _startLogoSequence();
  }

  void _startLogoSequence() async {
    // Show first logo
    setState(() {
      _showCurrentLogo = true;
    });

    // Wait for all logos to complete
    for (int i = 0; i < _logos.length; i++) {
      await Future.delayed(const Duration(milliseconds: 500));
      if (i < _logos.length - 1) {
        setState(() {
          _currentLogoIndex = i + 1;
        });
        await Future.delayed(const Duration(milliseconds: 2500));
      }
    }

    // Wait a bit more before navigating
    await Future.delayed(const Duration(milliseconds: 1000));

    if (mounted && !_navigated) {
      _navigated = true;
      Navigator.of(context).pushReplacementNamed('/');
    }
  }

  @override
  Widget build(BuildContext context) {
    final size = MediaQuery.of(context).size;
    
    if (_currentLogoIndex >= _logos.length) {
      return const SizedBox.shrink();
    }

    final currentLogo = _logos[_currentLogoIndex];
    final targetWidth = size.width * 0.8;
    final scale = targetWidth / currentLogo.originalWidth;
    final logoWidth = currentLogo.originalWidth * scale;
    final logoHeight = currentLogo.originalHeight * scale;

    return Scaffold(
      backgroundColor: Colors.black,
      body: SizedBox.expand(
        child: Center(
          child: AnimatedOpacity(
            opacity: _showCurrentLogo ? 1.0 : 0.0,
            duration: const Duration(milliseconds: 300),
            child: SaberGlowTracedLogo(
              svgPath: currentLogo.svgPath,
              width: logoWidth,
              height: logoHeight,
              glowColor: currentLogo.color,
              duration: const Duration(milliseconds: 2000),
            ),
          ),
        ),
      ),
    );
  }
}

class LogoConfig {
  final String svgPath;
  final double originalWidth;
  final double originalHeight;
  final Color color;

  LogoConfig({
    required this.svgPath,
    required this.originalWidth,
    required this.originalHeight,
    required this.color,
  });
}
